import React, { Component } from "react";
import EventForm from "./EventForm";
import ReviewPage from "./ReviewPage";
export class UserForm extends Component {
  state = {
    step: 1,
    event_name: "",
    event_description: "",
    org_name: "",
    location: "",
    address: "",
    cont_person: "",
    cont_phone: "",
    cont_email: "",
    start_date_time: "",
    end_date_time: "",
    cost: ""
  };

  // proceed to next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({
      step: step + 1
    });
  };

  // back to prev step
  prevStep = () => {
    const { step } = this.state;
    this.setState({
      step: step - 1
    });
  };

  // Handle fields change
  handleChange = input => e => {
    this.setState({ [input]: e.target.value });
  };

  render() {
    const { step } = this.state;
    const {
      event_name,
      event_description,
      org_name,
      location,
      address,
      cont_email,
      cont_person,
      cont_phone,
      start_date_time,
      end_date_time,
      cost
    } = this.state;
    const values = {
      event_name,
      event_description,
      org_name,
      location,
      address,
      cont_email,
      cont_person,
      cont_phone,
      start_date_time,
      end_date_time,
      cost
    };
    switch (step) {
      case 1:
        return (
          <EventForm
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 2:
        return (
          <ReviewPage
            nextStep={this.nextStep}
            prevStep={this.prevStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 3:
        return <h1>case3</h1>;
    }
  }
}

export default UserForm;
