import React, { Component } from "react";
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import AppBar from "material-ui/AppBar";
import TextField from "material-ui/TextField";
import RaisedButton from "material-ui/RaisedButton";
import getMuiTheme from "material-ui/styles/getMuiTheme";
import { red500, red100 } from "material-ui/styles/colors";
import muiThemeable from "material-ui/styles/muiThemeable";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

//change the color of theme
const muiTheme = getMuiTheme({
  palette: {
    primary1Color: "#e74c3c",
    accent1Color: red100
  }
});

class EventForm extends Component {
  continute = e => {
    e.preventDefault();
    this.props.nextStep();
  };

  render() {
    const { values, handleChange } = this.props;
    return (
      <MuiThemeProvider muiTheme={muiTheme}>
        <React.Fragment>
          <AppBar title="ActoKids" />
          <TextField
            hintText="Enter the name of event"
            floatingLabelText="Event Name"
            onChange={handleChange("event_name")}
            defaultValue={values.event_name}
          />

          <TextField
            style={styles.text}
            hintText="Enter a short description of event"
            floatingLabelText="Activity Description"
            onChange={handleChange("event_description")}
            defaultValue={values.event_description}
          />
          <br />
          <TextField
            hintText="Enter the Organization name"
            floatingLabelText="Organization Name"
            onChange={handleChange("org_name")}
            defaultValue={values.org_name}
          />

          <TextField
            style={styles.text}
            hintText="e.g. Seattle,WA"
            floatingLabelText="Location"
            onChange={handleChange("location")}
            defaultValue={values.location}
          />
          <br />
          <TextField
            hintText="e.g. 123 Main St"
            floatingLabelText="Address"
            onChange={handleChange("address")}
            defaultValue={values.address}
          />

          <TextField
            style={styles.text}
            hintText="Name of contact person"
            floatingLabelText="Contact Person"
            onChange={handleChange("cont_person")}
            defaultValue={values.cont_person}
          />
          <br />
          <TextField
            hintText="Contact phone number"
            floatingLabelText="Contact Phone"
            onChange={handleChange("cont_phone")}
            defaultValue={values.cont_phone}
          />

          <TextField
            style={styles.text}
            hintText="e.g. 1/1/2019 18:00"
            floatingLabelText="Start Date and Time"
            onChange={handleChange("start_date_time")}
            defaultValue={values.start_date_time}
          />
          <br />
          <TextField
            hintText="e.g. 1/3/2019 19:00"
            floatingLabelText="End Date and Time"
            onChange={handleChange("end_date_time")}
            defaultValue={values.end_date_time}
          />

          <TextField
            style={styles.text}
            hintText="e.g. $100"
            floatingLabelText="Cost"
            onChange={handleChange("cost")}
            defaultValue={values.cost}
          />
          <br />

          <InputLabel htmlFor="age-simple">Age</InputLabel>
          <Select
            inputProps={{
              name: "age",
              id: "age-simple"
            }}
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>

          <RaisedButton
            label="Submit"
            primary={true}
            style={styles.button}
            onClick={this.continue}
          />
        </React.Fragment>
      </MuiThemeProvider>
    );
  }
}

const styles = {
  button: {
    margin: 15
  },
  text: {
    margin: 20
  }
};

export default EventForm;
